const LanguageValue={Russian:"ru",English:"en"},languageButton=document.querySelector(".language-button"),changeLanguage=()=>{switch(document.documentElement.lang){case LanguageValue.Russian:document.documentElement.setAttribute("lang",LanguageValue.English);break;case LanguageValue.English:document.documentElement.setAttribute("lang",LanguageValue.Russian)}},onChangeLanguageButtonClickHandler=()=>{changeLanguage()};languageButton&&languageButton.addEventListener("click",onChangeLanguageButtonClickHandler);